package Logical_Codes;

public class EvenNumbers { 

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=1; 
		
		while(i<=10)
		{
			
			if(i==1)
				System.out.print("Even Numbers 1 to 10: ");
			
			if(i%2==0)
			System.out.print(i+" ");
			i++;
			
		}

	}

}


// o/p - Even Numbers 1 to 10: 2 4 6 8 10 