package Print_Star_Pattern;

public class HashDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int i=1; 
		
		while(i<=6)
		{
			if(i%2==0)
				System.out.println("####");
			else
				System.out.println(" #");
			i++;
			
		}

	}

}

