package Day_Dec02;

public class EscapeSequence {

	public static void main(String[] args) {
		// TODO Auto-generated method stub


		//We are on Earth" planet
		
		System.out.println("We are on Earth \" planet \n");
		
		// Backslash (\) nullifies subsequent character's significance
		
		// This is escape sequence character \
		System.out.println("\"This is escape sequence charaacter \\\"  \n");

		// This is escape sequence character \\
		System.out.println("This is escape sequence charaacter \\\\");
		
		System.out.println("This is my name");
		// \t : tab character: collection of white spaces/spaces
		System.out.println("This\tis\tmy\tname");
	}

}
